const players = {};

const config = {
  type: Phaser.HEADLESS,
  parent: 'phaser-example',
  width: 800,
  height: 600,
  physics: {
    default: 'arcade',
    arcade: {
      debug: false,
      gravity: {
        y: 0
      }
    }
  },
  scene: {
    preload: preload,
    create: create,
    update: update
  },
  autoFocus: false
};

function preload() {
  this.load.spritesheet('perso', 'http://localhost/phaser3/node_modules/phaser3-project-template/server/authoritative_server/assets/kicksAttack.png', {
    frameWidth: 75.5,
    frameHeight: 10,
    endFrame: 4
  });

}

function create() {
  const self = this;

  this.anims.create({
    key: "perso_anim",
    frames: this.anims.generateFrameNumbers('perso', {}),
    frameRate: 6,
    repeat: 0
  });


// this.fix.scale = 0.30;
  // this.fix.play("perso_anim");


  this.players = this.physics.add.group();

  this.physics.add.collider(this.players);

  this.physics.add.overlap(this.players, function(star, player) {});

  io.on('connection', function(socket) {
    console.log('a user connected');
    // create a new player and add it to our players object
    players[socket.id] = {
      scale: 0.38,
      x: Math.floor(Math.random() * 700) + 50,
      y: 447,
      playerId: socket.id,
      input: {
        left: false,
        right: false,
        up: false,
        down: false
      }
    };
    // add player to server
    addPlayer(self, players[socket.id]);
    // send the players object to the new player
    socket.emit('currentPlayers', players);
    // update all other players of the new player
    socket.broadcast.emit('newPlayer', players[socket.id]);
    // send the star object to the new player
    socket.emit('updateScore', self.scores);

    socket.on('disconnect', function() {
      console.log('user disconnected');
      // remove player from server
      removePlayer(self, socket.id);
      // remove this player from our players object
      delete players[socket.id];
      // emit a message to all players to remove this player
      io.emit('disconnect', socket.id);
    });

    // when a player moves, update the player data
    socket.on('playerInput', function(inputData) {
      handlePlayerInput(self, socket.id, inputData);
    });
  });
}

function update() {
  this.players.getChildren().forEach((player) => {
    const input = players[player.playerId].input;
    player.setVelocity(0);

    input.left ? (player.setVelocityX(-300), player.flipX = true) :
      input.right ? (player.setVelocityX(300), player.flipX = false) :
      player.setVelocityX(0)

    //bigger
    if (input.up && player.scale >= 0.223) {
      player.scale = player.scale - 0.003;
      player.y -= 2;
    }

    //smaller
    if (input.down && player.scale <= 2) {
      player.scale = player.scale + 0.003;
      player.y += 2;
    }

    /*
    if (input.a) {
      console.log('hahahahah');
    }
    */

    players[player.playerId].x = player.x;
    players[player.playerId].y = player.y;
    players[player.playerId].scale = player.scale;
    players[player.playerId].flipX = player.flipX;
  });
  this.physics.world.wrap(this.players, 5);
  io.emit('playerUpdates', players);
}

function randomPosition(max) {
  return Math.floor(Math.random() * max) + 50;
}

function handlePlayerInput(self, playerId, input) {
  self.players.getChildren().forEach((player) => {
    if (playerId === player.playerId) players[player.playerId].input = input
  });
}

function addPlayer(self, playerInfo) {
  const player = self.physics.add.image(playerInfo.x, playerInfo.y, 'perso').setScale(0.38) /*.setOrigin(0.5, 0.5).setDisplaySize(136, 392)*/ ;
  player.setMaxVelocity(200);
  player.playerId = playerInfo.playerId;
  self.players.add(player);
}

function removePlayer(self, playerId) {
  self.players.getChildren().forEach((player) => {
    if (playerId === player.playerId) player.destroy()
  });
}

const game = new Phaser.Game(config);
window.gameLoaded();
