import 'phaser';

class Chambre extends Phaser.Scene {
  //identifiant
  constructor() {
    super({key: "Chambre"});
  }
  //charge image
  preload() {
    this.load.image('bg','assets/bg.png');
  }

  create() {
    // this.image = this.add.image(400,300,'bg');

    var bg = this.add.image(750, 350, 'bg');
  }
}


class Personnage extends Phaser.Scene {
  //identifiant
  constructor() {
    super({key: "Personnage"});
  }
  //charge image
  preload() {
    this.load.image('perso','assets/profil.png');
  }

  create() {
    this.image = this.add.image(800,300,'perso');

    // var bg = this.add.image(750, 350, 'perso');
  }
}

var config = {
    type: Phaser.AUTO,
    width: 1500,
    height: 720,
    scene: [ Chambre ]
    // scene: {
        // preload: preload,
        // create: create
    // }
};

var game = new Phaser.Game(config);
