
var app = require('http').createServer(handler),
  io = require('socket.io')(app),
  fs = require('fs')


app.listen(8000);

var playerlocation = 0;
var playerlist = [];



function handler(req, res) {
  fs.readFile(__dirname + '/index.html',
    function(err, data) {
      if (err) {
        res.writeHead(500);
        return res.end('Error loading index.html');
      }

      res.writeHead(200);
      res.end(data);
    });
}

